<script>
  function checkcurrency(){
  		var c = $('.geolizr-currency-switcher-value').text();
    
    
  	if (c == 'ARS') {
      $('#ee').text('$955.00 ARS');
  		$('.envioaproxcurrency').hide();
      $('#envio-ars').show();
      
    } else if (c == 'BOB') {
      $('#ee').text('327,63 BOB');
  		$('.envioaproxcurrency').hide();
      $('#envio-bob').show();
      
    } else if (c == 'CLP') {
      $('#ee').text('$28.260,54 CLP');
  		$('.envioaproxcurrency').hide();
      $('#envio-clp').show();
      
    } else if (c == 'COP') {
      $('#ee').text('$128.421,81 COP');
  		$('.envioaproxcurrency').hide();
      $('#envio-cop').show();
      
    } else if ( c == 'CRC') {
      $('#ee').text('₡26.811,63 CRC');
  		$('.envioaproxcurrency').hide();
      $('#envio-crc').show();
      
    } else if (c == 'GTQ') {
      $('#ee').text('Q349,81 GTQ');
  		$('.envioaproxcurrency').hide();
      $('#envio-gtq').show();

    } else if (c == 'PYG') {
       $('#ee').text('GS. 262.379,29 PYG');
  		$('.envioaproxcurrency').hide();
      $('#envio-pyg').show();
      
    } else if (c == 'PEN') {
      $('#ee').text('S/. 152,38 PEN');
  		$('.envioaproxcurrency').hide();
      $('#envio-pen').show();
      
    } else if (c == 'UYU') {
       $('#ee').text('$1.335,37');
  		$('.envioaproxcurrency').hide();
      $('#envio-uyu').show();
      
    } else if (c == 'VEF') {
      $('#ee').text('BS. 2.335.592,90');
  		$('.envioaproxcurrency').hide();
      $('#envio-vef').show();
      
    } else {
      $('#ee').text('$109.00 MXN');
    	$('.envioaproxcurrency').hide();
      $('#envio-mxn').show();
    }
  }
  $(document).ready(function(){
  	checkcurrency();
  });
  $(document).on('click', '.geolizr-currency-list-link', function(){
  	 checkcurrency();
    
  });  
</script>

