  	var cookieCurrency = 'HNL';
  } else if (location.hash === "#USD") {
  	var cookieCurrency = 'USD';
  } else if (location.hash === "#NIO") {
  	var cookieCurrency = 'NIO';
  } else if (location.hash === "#CRC") {
  	var cookieCurrency = 'CRC';
  } else if (location.hash === "#PAB") {
